package LabAss;

public class Ass8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		char ch='6';
		// Check if character is a letter (A-Z or a-z)
		if ((ch >= 'A' && ch <= 'Z') || (ch >= 'a' && ch <= 'z')) {
			System.out.println(ch + " is a Letter.");
		} else {
			System.out.println(ch + " is Not a Letter.");
		}
		
		
	}
}
