package LabAssNestedIf;

public class Ass3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		int num = 8;

		if (num % 2 == 0) {
		    if (num > 5) {
		        System.out.println("The number is even and greater than 5.");
		    } else {
		        System.out.println("The number is even but not greater than 5.");
		    }
		} else {
		    System.out.println("The number is odd.");
		}
		
	}
}
